import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useTheme } from "@/contexts/theme-context";
import { Moon, Sun, Monitor } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export function ThemeToggle() {
  const { theme, resolvedTheme, setTheme } = useTheme();

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="ghost" 
          size="icon"
          className="relative w-10 h-10 rounded-lg transition-all duration-300 hover:bg-primary/10"
          data-testid="button-theme-toggle"
        >
          <AnimatePresence mode="wait">
            {resolvedTheme === "dark" ? (
              <motion.div
                key="moon"
                initial={{ rotate: -90, opacity: 0 }}
                animate={{ rotate: 0, opacity: 1 }}
                exit={{ rotate: 90, opacity: 0 }}
                transition={{ duration: 0.3, ease: "easeInOut" }}
                className="absolute inset-0 flex items-center justify-center"
              >
                <Moon className="h-5 w-5 text-primary" />
              </motion.div>
            ) : (
              <motion.div
                key="sun"
                initial={{ rotate: 90, opacity: 0 }}
                animate={{ rotate: 0, opacity: 1 }}
                exit={{ rotate: -90, opacity: 0 }}
                transition={{ duration: 0.3, ease: "easeInOut" }}
                className="absolute inset-0 flex items-center justify-center"
              >
                <Sun className="h-5 w-5 text-primary" />
              </motion.div>
            )}
          </AnimatePresence>
          <span className="sr-only">Toggle theme</span>
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent 
        align="end"
        className="min-w-[140px] glass dark:glass-dark"
      >
        <DropdownMenuItem 
          onClick={() => setTheme("light")}
          className="cursor-pointer"
          data-testid="menu-theme-light"
        >
          <Sun className="mr-2 h-4 w-4" />
          <span>Light</span>
          {theme === "light" && (
            <motion.div
              layoutId="theme-indicator"
              className="ml-auto w-2 h-2 rounded-full bg-primary"
              transition={{ type: "spring", stiffness: 300, damping: 30 }}
            />
          )}
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => setTheme("dark")}
          className="cursor-pointer"
          data-testid="menu-theme-dark"
        >
          <Moon className="mr-2 h-4 w-4" />
          <span>Dark</span>
          {theme === "dark" && (
            <motion.div
              layoutId="theme-indicator"
              className="ml-auto w-2 h-2 rounded-full bg-primary"
              transition={{ type: "spring", stiffness: 300, damping: 30 }}
            />
          )}
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => setTheme("system")}
          className="cursor-pointer"
          data-testid="menu-theme-system"
        >
          <Monitor className="mr-2 h-4 w-4" />
          <span>System</span>
          {theme === "system" && (
            <motion.div
              layoutId="theme-indicator"
              className="ml-auto w-2 h-2 rounded-full bg-primary"
              transition={{ type: "spring", stiffness: 300, damping: 30 }}
            />
          )}
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/contexts/theme-context.tsx.content:
import { createContext, useContext, useEffect, useState } from "react";

type Theme = "light" | "dark" | "system";
type ResolvedTheme = "light" | "dark";

interface ThemeContextType {
  theme: Theme;
  resolvedTheme: ResolvedTheme;
  setTheme: (theme: Theme) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [theme, setTheme] = useState<Theme>(() => {
    // Get saved theme from localStorage or default to system
    const saved = localStorage.getItem("theme") as Theme;
    return saved || "system";
  });

  const [resolvedTheme, setResolvedTheme] = useState<ResolvedTheme>("light");

  useEffect(() => {
    // Function to apply theme to document
    const applyTheme = (theme: Theme) => {
      let resolved: ResolvedTheme;

      if (theme === "system") {
        // Check system preference
        const systemPrefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
        resolved = systemPrefersDark ? "dark" : "light";
      } else {
        resolved = theme as ResolvedTheme;
      }

      // Apply or remove dark class
      const root = document.documentElement;
      if (resolved === "dark") {
        root.classList.add("dark");
      } else {
        root.classList.remove("dark");
      }

      setResolvedTheme(resolved);
    };

    // Apply theme on mount and when theme changes
    applyTheme(theme);

    // Save theme preference to localStorage
    if (theme !== "system") {
      localStorage.setItem("theme", theme);
    } else {
      localStorage.removeItem("theme");
    }

    // Listen for system theme changes if using system theme
    if (theme === "system") {
      const mediaQuery = window.matchMedia("(prefers-color-scheme: dark)");
      const handleChange = () => applyTheme(theme);

      // Add event listener
      mediaQuery.addEventListener("change", handleChange);

      // Cleanup
      return () => mediaQuery.removeEventListener("change", handleChange);
    }
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, resolvedTheme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error("useTheme must be used within a ThemeProvider");
  }
  return context;
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-seo.ts.content:
import { useEffect } from "react";

interface SEOProps {
  title: string;
  description: string;
  path: string;
  ogImage?: string;
  keywords?: string;
  author?: string;
  structuredData?: any;
  alternates?: { lang: string; href: string }[];
  articlePublishedTime?: string;
  articleModifiedTime?: string;
  twitterHandle?: string;
  noindex?: boolean;
  additionalMetaTags?: { name?: string; property?: string; content: string }[];
}

export function useSEO({ 
  title, 
  description, 
  path, 
  ogImage,
  keywords,
  author = "AltafToolsHub",
  structuredData,
  alternates,
  articlePublishedTime,
  articleModifiedTime,
  twitterHandle = "@altaftoolshub",
  noindex = false,
  additionalMetaTags = []
}: SEOProps) {
  useEffect(() => {
    // Set document title
    document.title = title;

    // Helper function to update or create meta tags
    const updateMetaTag = (selector: string, attribute: string, value: string) => {
      let element = document.querySelector(selector) as HTMLMetaElement | HTMLLinkElement;
      if (!element) {
        if (selector.includes('link')) {
          element = document.createElement('link');
        } else {
          element = document.createElement('meta');
        }
        const [, attr, val] = selector.match(/\[([^=]+)="([^"]+)"\]/) || [];
        if (attr && val) {
          element.setAttribute(attr, val);
        }
        document.head.appendChild(element);
      }
      element.setAttribute(attribute, value);
    };

    // Basic meta tags
    updateMetaTag('meta[name="description"]', 'content', description);

    if (keywords) {
      updateMetaTag('meta[name="keywords"]', 'content', keywords);
    }

    if (author) {
      updateMetaTag('meta[name="author"]', 'content', author);
    }

    // Robots meta tag
    if (noindex) {
      updateMetaTag('meta[name="robots"]', 'content', 'noindex, nofollow');
    } else {
      updateMetaTag('meta[name="robots"]', 'content', 'index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1');
    }

    // Canonical URL
    updateMetaTag('link[rel="canonical"]', 'href', `https://www.altaftoolshub.com${path}`);

    // Open Graph tags
    const ogTags = [
      { property: 'og:title', content: title },
      { property: 'og:description', content: description },
      { property: 'og:url', content: `https://www.altaftoolshub.com${path}` },
      { property: 'og:type', content: articlePublishedTime ? 'article' : 'website' },
      { property: 'og:site_name', content: 'AltafToolsHub' },
      { property: 'og:locale', content: 'en_US' },
    ];

    if (ogImage) {
      ogTags.push(
        { property: 'og:image', content: ogImage },
        { property: 'og:image:alt', content: title },
        { property: 'og:image:width', content: '1200' },
        { property: 'og:image:height', content: '630' }
      );
    }

    if (articlePublishedTime) {
      ogTags.push({ property: 'article:published_time', content: articlePublishedTime });
    }

    if (articleModifiedTime) {
      ogTags.push({ property: 'article:modified_time', content: articleModifiedTime });
    }

    ogTags.forEach(({ property, content }) => {
      let ogTag = document.querySelector(`meta[property="${property}"]`);
      if (!ogTag) {
        ogTag = document.createElement('meta');
        ogTag.setAttribute('property', property);
        document.head.appendChild(ogTag);
      }
      ogTag.setAttribute('content', content);
    });

    // Twitter Card tags
    const twitterTags = [
      { name: 'twitter:card', content: ogImage ? 'summary_large_image' : 'summary' },
      { name: 'twitter:title', content: title },
      { name: 'twitter:description', content: description },
      { name: 'twitter:site', content: twitterHandle },
      { name: 'twitter:creator', content: twitterHandle },
    ];

    if (ogImage) {
      twitterTags.push({ name: 'twitter:image', content: ogImage });
      twitterTags.push({ name: 'twitter:image:alt', content: title });
    }

    twitterTags.forEach(({ name, content }) => {
      let twitterTag = document.querySelector(`meta[name="${name}"]`);
      if (!twitterTag) {
        twitterTag = document.createElement('meta');
        twitterTag.setAttribute('name', name);
        document.head.appendChild(twitterTag);
      }
      twitterTag.setAttribute('content', content);
    });

    // Additional meta tags
    additionalMetaTags.forEach(({ name, property, content }) => {
      const selector = name ? `meta[name="${name}"]` : `meta[property="${property}"]`;
      const attribute = name ? 'name' : 'property';
      let tag = document.querySelector(selector);
      if (!tag) {
        tag = document.createElement('meta');
        tag.setAttribute(attribute, name || property || '');
        document.head.appendChild(tag);
      }
      tag.setAttribute('content', content);
    });

    // Alternate language links
    if (alternates && alternates.length > 0) {
      // Remove existing alternate links
      document.querySelectorAll('link[rel="alternate"]').forEach(link => link.remove());

      alternates.forEach(({ lang, href }) => {
        const link = document.createElement('link');
        link.setAttribute('rel', 'alternate');
        link.setAttribute('hreflang', lang);
        link.setAttribute('href', href);
        document.head.appendChild(link);
      });
    }

    // Structured Data (JSON-LD)
    if (structuredData) {
      // Remove existing structured data scripts
      const existingScripts = document.querySelectorAll('script[type="application/ld+json"]');
      existingScripts.forEach(script => script.remove());

      // Handle array of structured data or single object
      const schemas = Array.isArray(structuredData) ? structuredData : [structuredData];

      // Add new structured data
      schemas.forEach((schema, index) => {
        const script = document.createElement('script');
        script.type = 'application/ld+json';
        script.setAttribute('data-schema-index', index.toString());
        script.textContent = JSON.stringify(schema);
        document.head.appendChild(script);
      });
    }

    // Add site verification tags (these should be static but just in case)
    const verificationTags = [
      { name: 'google-site-verification', content: 'your-google-verification-code' },
      { name: 'msvalidate.01', content: 'your-bing-verification-code' },
    ];

    verificationTags.forEach(({ name, content }) => {
      if (content !== 'your-google-verification-code' && content !== 'your-bing-verification-code') {
        updateMetaTag(`meta[name="${name}"]`, 'content', content);
      }
    });

    // Cleanup function to remove dynamically added elements when component unmounts
    return () => {
      // Only remove structured data script on unmount
      const structuredDataScript = document.querySelector('script[type="application/ld+json"]');
      if (structuredDataScript) {
        structuredDataScript.remove();
      }
    };
  }, [
    title, 
    description, 
    path, 
    ogImage, 
    keywords, 
    author, 
    structuredData,
    alternates,
    articlePublishedTime,
    articleModifiedTime,
    twitterHandle,
    noindex,
    additionalMetaTags
  ]);
}

