import { useToast } from "@/hooks/use-toast";
import { useSEO, generateHowToSchema, generateSoftwareApplicationSchema } from "@/hooks/use-seo";
import { 
  analyzeText, 
  enhanceText, 
  checkGrammar, 
  checkSpelling 
} from "@/lib/text-enhancement";
