  --chart-1: hsl(203, 88%, 53%);
  --chart-2: hsl(160, 100%, 36%);
  --chart-3: hsl(42, 93%, 56%);
  --chart-4: hsl(147, 79%, 42%);
  --chart-5: hsl(341, 75%, 51%);
  --sidebar: hsl(222, 47%, 11%);
  --sidebar-foreground: hsl(210, 40%, 98%);
  --sidebar-primary: hsl(263, 70%, 65%);
  --sidebar-primary-foreground: hsl(0, 0%, 100%);
  --sidebar-accent: hsl(217, 91%, 15%);
  --sidebar-accent-foreground: hsl(210, 40%, 98%);
  --sidebar-border: hsl(217, 33%, 17%);
  --sidebar-ring: hsl(263, 70%, 65%);
}

@layer base {
  * {
    @apply border-border;
  }

  /* Global smooth scrolling */
  html {
    scroll-behavior: smooth;
    scroll-padding-top: 5rem; /* Account for sticky header */
  }

  /* Respect reduced motion preferences */
  @media (prefers-reduced-motion: reduce) {
    html {
      scroll-behavior: auto;
    }

    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
    }

    /* Disable specific animations that could cause motion sickness */
    .hero-gradient::before,
    .hero-gradient::after,
    .hero-circuit {
      animation: none !important;
    }

    /* Still allow focus and hover states but without transitions */
    button:focus-visible,
    a:focus-visible,
    [tabindex]:focus-visible {
      outline: 2px solid hsl(var(--ring));
      outline-offset: 2px;
      transition: none;
    }
  }

  /* User-controlled reduced motion via data attribute */
  [data-reduced-motion="true"] *,
  [data-reduced-motion="true"] *::before,
  [data-reduced-motion="true"] *::after {
    animation: none !important;
    transition: none !important;
    transition-duration: 0ms !important;
    transition-delay: 0ms !important;
    scroll-behavior: auto !important;
    transform: none !important;
  }

  /* Disable hover transformations when reduced motion is active */
  [data-reduced-motion="true"] .group-hover\\:scale-110,
  [data-reduced-motion="true"] .group-hover\\:scale-105,
  [data-reduced-motion="true"] .group-hover\\:rotate-3,
  [data-reduced-motion="true"] .hover\\:scale-105,
  [data-reduced-motion="true"] .hover\\:scale-110 {
    transform: none !important;
  }

  [data-reduced-motion="true"] .hero-gradient::before,
  [data-reduced-motion="true"] .hero-gradient::after,
  [data-reduced-motion="true"] .hero-circuit {
    animation: none !important;
  }

  /* Disable all Tailwind animations when reduced motion is active */
  [data-reduced-motion="true"] .animate-pulse,
  [data-reduced-motion="true"] .animate-bounce,
  [data-reduced-motion="true"] .animate-spin,
  [data-reduced-motion="true"] .animate-ping,
  [data-reduced-motion="true"] .animate-blob,
  [data-reduced-motion="true"] .animate-float,
  [data-reduced-motion="true"] .animate-float-delayed {
    animation: none !important;
  }

  /* Enhanced system preference rules */
  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation: none !important;
      transition-duration: 0ms !important;
      transition-delay: 0ms !important;
      scroll-behavior: auto !important;
    }

    .animate-pulse,
    .animate-bounce,
    .animate-spin,
    .animate-ping,
    .animate-blob,
    .animate-float,
    .animate-float-delayed {
      animation: none !important;
    }
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    min-height: 100vh;
    font-feature-settings: "kern" 1;
    text-rendering: optimizeLegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  /* Typography hierarchy - Mobile-first fluid responsive sizing */
  h1 {
    @apply text-4xl sm:text-5xl lg:text-6xl font-bold tracking-tight;
    line-height: 1.1;
  }

  h2 {
    @apply text-2xl sm:text-3xl lg:text-4xl font-semibold tracking-tight;
    line-height: 1.2;
  }

  h3 {
    @apply text-xl sm:text-2xl font-semibold;
    line-height: 1.3;
  }

  h4 {
    @apply text-lg sm:text-xl font-medium;
    line-height: 1.4;
  }

  p {
    @apply text-base leading-relaxed;
  }

  /* Ensure minimum touch target size on mobile for interactive elements */
  button, 
  a[role="button"], 
  [data-testid*="button"], 
  [data-testid*="link"],
  .btn {
    min-height: 44px;
    min-width: 44px;
    touch-action: manipulation;
  }

  @media (min-width: 640px) {
    button, 
    a[role="button"], 
    [data-testid*="button"], 
    [data-testid*="link"],
    .btn {
      min-height: 40px;
      min-width: 40px;
    }
  }

  /* Card standardization */
  .card-standard {
    @apply p-6 rounded-lg;
  }

  /* Text truncation utilities */
  .truncate-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
  }

  .truncate-3 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
  }

  /* Container utilities for consistent spacing */
  .container-section {
    @apply container mx-auto px-4;
  }

  /* Grid layouts - mobile-first */
  .grid-responsive {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6;
  }

  .grid-tools {
    @apply grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6;
  }

  /* Prevent horizontal overflow */
  html, body {
    overflow-x: hidden;
    max-width: 100vw;
  }

  main, section {
    overflow-x: hidden;
  }

  /* Fix for mobile viewport units */
  @supports (height: 100dvh) {
    .min-h-screen {
      min-height: 100dvh;
    }
  }
}

/* Custom Scrollbar Styles */
.scrollbar-thin {
  scrollbar-width: thin;
}

.scrollbar-thumb-primary\/20 {
  scrollbar-color: rgba(var(--primary), 0.2) transparent;
}

/* Enhanced Webkit Scrollbar Styles */
.scrollbar-thin::-webkit-scrollbar,
.custom-scrollbar::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}

.scrollbar-thin::-webkit-scrollbar-track,
.custom-scrollbar::-webkit-scrollbar-track {
  background: transparent;
  border-radius: 10px;
}

.scrollbar-thin::-webkit-scrollbar-thumb,
.custom-scrollbar::-webkit-scrollbar-thumb {
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.3), rgba(59, 130, 246, 0.2));
