/* Premium button gradient with enhanced effects */
.btn-gradient {
  background: linear-gradient(135deg, #8B5CF6 0%, #3B82F6 100%);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  box-shadow: 
    0 4px 15px 0 rgba(139, 92, 246, 0.3),
