}

.btn-gradient::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s ease;
}

.btn-gradient:hover {
  background: linear-gradient(135deg, #7C3AED 0%, #2563EB 100%);
  transform: translateY(-2px) scale(1.02);
  box-shadow: 
    0 7px 25px -5px rgba(139, 92, 246, 0.5),
    0 15px 35px -10px rgba(59, 130, 246, 0.3),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.3);
}

.btn-gradient:hover::before {
  left: 100%;
}

.btn-gradient:active {
  transform: translateY(0) scale(1);
}

.gradient-text-secondary {
  background: linear-gradient(135deg, #14B8A6 0%, #0D9488 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Dark Techy Hero Gradient with Professional Animation */
.hero-gradient {
  background: #000000;
  background: linear-gradient(180deg, #000000 0%, #001133 50%, #000511 100%);
  position: relative;
  overflow: hidden;
  z-index: 1 !important; /* Very low to stay below navigation */
  isolation: isolate; /* Create new stacking context to prevent interference */
}

/* Animated Tech Grid Pattern */
.hero-gradient::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    linear-gradient(rgba(0, 136, 255, 0.03) 2px, transparent 2px),
    linear-gradient(90deg, rgba(0, 136, 255, 0.03) 2px, transparent 2px);
  background-size: 100px 100px;
  animation: gridSlide 20s linear infinite;
  z-index: 0 !important; /* Below hero content */
  pointer-events: none;
}

/* Glowing Blue Energy Orbs */
.hero-gradient::after {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  right: -50%;
  bottom: -50%;
  background: 
    radial-gradient(circle at 20% 50%, rgba(0, 136, 255, 0.15), transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(0, 102, 204, 0.1), transparent 50%),
    radial-gradient(circle at 40% 20%, rgba(0, 168, 255, 0.1), transparent 50%),
    radial-gradient(circle at 70% 40%, rgba(0, 128, 255, 0.08), transparent 50%);
  animation: techPulse 15s ease-in-out infinite;
  z-index: 0 !important; /* Below hero content */
  pointer-events: none;
  filter: blur(40px);
}

/* Tech Circuit Lines Animation */
.hero-circuit {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0 !important; /* Below hero content */
  opacity: 0.3;
  background-image: 
    repeating-linear-gradient(
      90deg,
      transparent,
      transparent 200px,
      rgba(0, 136, 255, 0.1) 200px,
      rgba(0, 136, 255, 0.1) 201px
    ),
    repeating-linear-gradient(
      0deg,
      transparent,
      transparent 200px,
      rgba(0, 136, 255, 0.1) 200px,
      rgba(0, 136, 255, 0.1) 201px
    );
  animation: circuitFlow 10s linear infinite;
  pointer-events: none;
}

@keyframes gridSlide {
  0% { transform: translate(0, 0); }
  100% { transform: translate(100px, 100px); }
}

@keyframes techPulse {
  0%, 100% { 
    transform: scale(1) rotate(0deg);
    filter: blur(40px) brightness(1);
  }
  25% {
    transform: scale(1.1) rotate(90deg);
    filter: blur(45px) brightness(1.2);
  }
  50% { 
    transform: scale(1.05) rotate(180deg);
    filter: blur(35px) brightness(1.1);
  }
  75% {
    transform: scale(0.95) rotate(270deg);
    filter: blur(50px) brightness(0.9);
  }
}

@keyframes circuitFlow {
  0% { 
    transform: translateX(0);
    opacity: 0.3;
  }
  50% {
    opacity: 0.5;
  }
  100% { 
    transform: translateX(20px);
    opacity: 0.3;
  }
}

/* Hero Content with Enhanced Visibility */
.hero-content {
  position: relative;
  z-index: 2 !important; /* Above animations but below header */
  color: #ffffff;
}

/* Enhanced Text with Blue Glow */
.hero-content h1 {
