}

/* Blue Gradient Buttons for Hero */
.hero-btn-primary {
  background: linear-gradient(135deg, #0066cc 0%, #00a8ff 100%);
  box-shadow: 
    0 4px 20px rgba(0, 168, 255, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.2);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.hero-btn-primary::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s ease;
}

.hero-btn-primary:hover {
  background: linear-gradient(135deg, #0052a3 0%, #0095e8 100%);
  transform: translateY(-2px) scale(1.02);
  box-shadow: 
    0 6px 30px rgba(0, 168, 255, 0.6),
    0 12px 48px rgba(0, 102, 204, 0.3),
