/* Floating animation for decorative elements */
@keyframes float {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  33% { transform: translateY(-10px) rotate(1deg); }
  66% { transform: translateY(10px) rotate(-1deg); }
}

@keyframes float-delayed {
  0%, 100% { transform: translateY(0px) translateX(0px); }
  25% { transform: translateY(-15px) translateX(5px); }
  50% { transform: translateY(5px) translateX(-5px); }
  75% { transform: translateY(-5px) translateX(10px); }
}

/* Blob animation for background shapes */
@keyframes blob {
  0%, 100% { transform: translate(0px, 0px) scale(1); }
  33% { transform: translate(30px, -50px) scale(1.1); }
  66% { transform: translate(-20px, 20px) scale(0.9); }
}

.animate-blob {
  animation: blob 7s infinite;
}

.animation-delay-2000 {
  animation-delay: 2s;
}

.animation-delay-4000 {
  animation-delay: 4s;
}

/* Fixed Navigation z-index - Ensure dropdowns appear above everything */
header {
  position: sticky !important;
  top: 0;
  z-index: 10000 !important; /* High z-index for header but not maximum (dropdown needs to be above) */
  isolation: isolate !important; /* Create new stacking context */
}

/* Force dropdown container to highest level */
.multi-dropdown-container {
  position: relative !important;
  isolation: isolate !important; /* Create new stacking context */
}

/* Dropdown Menu Z-Index Fix - Maximum possible values */
.dropdown-menu-container {
  z-index: 2147483647 !important; /* Maximum possible z-index value */
  position: absolute !important;
  isolation: isolate !important;
}

.dropdown-menu-content {
  z-index: 2147483647 !important; /* Maximum possible z-index for content */
  position: relative !important;
  background: var(--background) !important; /* Ensure proper background */
}

/* Navigation z-index fixes to ensure dropdowns appear above all content */
[data-radix-navigation-menu] {
  z-index: 2147483647 !important;
}

[data-radix-popper-content] {
  z-index: 2147483647 !important;
}

/* Ensure navigation viewport and content have proper z-index */
[data-radix-navigation-menu-viewport] {
  z-index: 2147483647 !important;
}

[data-radix-navigation-menu-content] {
  z-index: 2147483647 !important;
}

/* Ensure dropdowns are always on top */
.navigation-menu {
  z-index: 2147483647 !important;
}

.navigation-menu-content {
  z-index: 2147483647 !important;
}

/* Global dropdown fix - ensure all dropdown containers are on top */
.dropdown-menu,
.dropdown-content,
[role="menu"],
[data-state="open"] {
  z-index: 2147483647 !important;
}

/* Sparkle animation */
@keyframes sparkle {
  0%, 100% { opacity: 0; transform: scale(0); }
  50% { opacity: 1; transform: scale(1); }
}

.animate-sparkle {
  animation: sparkle 3s ease-in-out infinite;
}

/* Shimmer animation for dropdown headers */
@keyframes shimmer-slow {
  0% { transform: translateX(-200%); }
  100% { transform: translateX(200%); }
}

.animate-shimmer-slow {
  animation: shimmer-slow 4s ease-in-out infinite;
}

/* Smooth fade in up animation */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in-up {
  animation: fadeInUp 0.6s cubic-bezier(0.4, 0, 0.2, 1) forwards;
}

/* Enhanced focus states for accessibility */
*:focus-visible {
  outline: 2px solid var(--primary);
  outline-offset: 2px;
  border-radius: 4px;
  transition: outline 0.2s ease;
}

/* Enhanced UX Micro-interactions */

/* Improved button interactions with better touch targets */
button, [role="button"], .btn {
  min-height: 44px; /* WCAG AA minimum touch target */
  min-width: 44px;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

/* Ripple effect for button interactions */
button:active::after, [role="button"]:active::after, .btn:active::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  transform: translate(-50%, -50%);
  animation: ripple 0.4s ease-out;
}

@keyframes ripple {
  to {
    width: 300px;
    height: 300px;
    opacity: 0;
  }
}

/* Enhanced card hover states */
.tool-card {
  transform: translateZ(0); /* Enable hardware acceleration */
  transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.tool-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.05), rgba(59, 130, 246, 0.05));
  opacity: 0;
  transition: opacity 0.3s ease;
  border-radius: inherit;
  z-index: -1;
}

.tool-card:hover::before {
  opacity: 1;
}

.dark .tool-card {
  background: rgba(30, 30, 40, 0.95);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Enhanced loading states */
@keyframes pulse-glow {
  0%, 100% {
    opacity: 1;
    box-shadow: 0 0 5px rgba(139, 92, 246, 0.5);
  }
  50% {
    opacity: 0.7;
    box-shadow: 0 0 20px rgba(139, 92, 246, 0.8);
  }
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

/* Better loading spinner */
.loading-spinner {
  width: 40px;
  height: 40px;
  border: 3px solid rgba(139, 92, 246, 0.2);
  border-top: 3px solid var(--primary);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Enhanced scroll-to-top behavior */
html {
  scroll-behavior: smooth;
  scroll-padding-top: 5rem;
}

/* Improved form interactions */
input, textarea, select {
  transition: all 0.2s ease;
  border-radius: 8px;
}

input:focus, textarea:focus, select:focus {
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(139, 92, 246, 0.15);
}

/* Enhanced navigation menu animations */
.nav-link {
  position: relative;
  transition: all 0.2s ease;
}

.nav-link::after {
  content: '';
  position: absolute;
  bottom: -2px;
  left: 50%;
  width: 0;
  height: 2px;
  background: linear-gradient(135deg, var(--primary), var(--secondary));
  transition: all 0.3s ease;
  transform: translateX(-50%);
}

.nav-link:hover::after {
  width: 100%;
}

