      "logo": {
        "@type": "ImageObject",
        "url": "https://www.altaftoolshub.com/logo.png"
      }
    },
    "author": {
      "@type": "Organization",
      "name": "AltafToolsHub",
      "url": "https://www.altaftoolshub.com"
    },
    "maintainer": {
      "@type": "Organization",
      "name": "AltafToolsHub",
      "url": "https://www.altaftoolshub.com"
    },
