// Enhanced Tool Card Component with premium animations
const ToolCard = ({ tool }: { tool: Tool }) => {
  const Icon = tool.icon;
  const { reducedMotion } = useReducedMotionContext();

  const cardContent = (
    <motion.div
      className="h-full"
