        whileHover: tool.available ? { scale: 1.03, y: -5 } : {},
        whileTap: tool.available ? { scale: 0.98 } : {},
        initial: { opacity: 0, y: 30 },
        whileInView: { opacity: 1, y: 0 },
        viewport: { once: true, amount: 0.2 },
        transition: { 
          duration: 0.5, 
          ease: [0.4, 0, 0.2, 1],
          hover: { duration: 0.3 }
        }
      }, {
        initial: { opacity: 1 },
        animate: { opacity: 1 },
        transition: { duration: 0 }
      })}
