          }
        ]}
      />

      <ComparisonSection
        toolName="Image to PDF Converter"
        comparisons={[
          { feature: "File Privacy", ourTool: "100% browser-based", others: "Upload to servers", highlight: true },
          { feature: "Supported Formats", ourTool: "JPG, PNG, WebP, GIF, BMP", others: "JPG, PNG only" },
