              </Alert>

              <Button 
                onClick={unlockPDF}
                disabled={!password.trim()}
                className="w-full btn-gradient text-white font-semibold"
                size="lg"
                data-testid="button-unlock"
              >
                <Unlock className="w-5 h-5 mr-2" />
                Unlock PDF
              </Button>
            </div>
          </Card>
