import { Card } from "@/components/ui/card";
import { Shield, Lock, Cookie, Mail, Globe, FileText, Users, Server } from "lucide-react";
import { useSEO } from "@/hooks/use-seo";
import { motion } from "framer-motion";

const sections = [
  {
    icon: Shield,
    title: "Our Privacy Commitment",
    content: [
      "At AltafToolsHub, your privacy is our top priority. We've built our entire platform around the principle of maximum privacy protection.",
      "All file processing happens directly in your browser using WebAssembly and JavaScript. Your files never leave your device, and we never have access to them.",
      "This privacy policy explains how we handle any information we collect and your rights regarding that information."
    ]
  },
  {
    icon: Server,
    title: "Data Collection & Processing",
    content: [
      "**Client-Side Processing Only**: All file manipulation, conversion, and compression happens entirely in your browser. We do not upload, store, or have access to any files you process using our tools.",
      "**No Server Storage**: Since all processing is client-side, your files are never transmitted to our servers. They remain on your device throughout the entire process.",
      "**Automatic Deletion**: Processed files exist only in your browser's memory during the session and are automatically cleared when you close the tab or navigate away."
    ]
  },
  {
    icon: Cookie,
    title: "Cookie Usage",
    content: [
      "We use cookies to improve your experience on our website. Here's how:",
      "**Necessary Cookies**: Essential for the website to function properly. These include session cookies and security cookies.",
      "**Analytics Cookies**: Help us understand how visitors interact with our website. We use privacy-focused analytics that don't track individual users.",
      "**Functional Cookies**: Remember your preferences such as theme selection (light/dark mode) and language settings.",
      "You can manage your cookie preferences at any time through the cookie consent banner or your browser settings."
    ]
  },
  {
    icon: Users,
    title: "Information We Collect",
    content: [
      "**Anonymous Analytics**: We collect aggregated, anonymous data about page views, tool usage, and general traffic patterns to improve our services.",
      "**Error Reports**: If our tools encounter an error, we may collect anonymous error logs to help us fix issues. These never contain your file data.",
      "**Voluntary Information**: Information you provide when contacting us via email for support or feedback.",
      "**No Personal Data from Files**: We never collect, see, or store any data from the files you process."
    ]
  },
  {
    icon: Lock,
    title: "Your Rights & Control",
    content: [
      "**Access**: You have the right to request information about any data we have about you (which is minimal by design).",
      "**Deletion**: You can request deletion of any information we might have about you.",
      "**Opt-Out**: You can opt out of analytics cookies at any time through our cookie preferences.",
      "**Data Portability**: You can request a copy of any data we have about you in a portable format.",
      "**No Account Required**: We don't require accounts or registration, further protecting your privacy."
    ]
  },
  {
    icon: Globe,
    title: "Third-Party Services",
    content: [
      "**Analytics**: We use privacy-focused analytics services that don't track individual users or use cookies for tracking.",
      "**CDN**: We use content delivery networks to serve our website faster, but they don't have access to your processed files.",
      "**No Advertising**: We don't use any advertising networks or tracking pixels.",
      "**No Social Media Tracking**: We don't use social media plugins that could track your activity."
    ]
  },
  {
    icon: FileText,
    title: "Data Security",
    content: [
      "**HTTPS Only**: Our entire website is served over HTTPS, ensuring encrypted connections.",
      "**Browser Sandbox**: File processing happens within your browser's security sandbox, isolated from other websites.",
      "**No Data Transmission**: Since files aren't uploaded, there's no risk of interception during transmission.",
      "**Open Source Libraries**: We use well-maintained, open-source libraries for file processing."
    ]
  },
  {
    icon: Mail,
    title: "Contact & Updates",
    content: [
      "**Privacy Questions**: For any privacy-related questions or concerns, contact us at altaftoolshub@gmail.com",
      "**Policy Updates**: We'll notify users of any significant changes to this privacy policy through a prominent notice on our website.",
      "**Response Time**: We aim to respond to all privacy-related inquiries within 48 hours.",
      "**Transparency Reports**: We commit to transparency about any data requests we receive (though we have no user data to share)."
    ]
  }
];

export default function PrivacyPolicy() {
  useSEO({
    title: "Privacy Policy - AltafToolsHub | Your Privacy is Our Priority",
    description: "Learn how AltafToolsHub protects your privacy with 100% client-side file processing. No uploads, no tracking, complete privacy.",
    path: "/privacy"
  });

  return (
    <div className="min-h-screen pattern-bg">
      <div className="container mx-auto px-4 py-16">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="max-w-4xl mx-auto"
        >
          {/* Header */}
          <div className="text-center mb-12">
            <div className="inline-flex items-center justify-center w-20 h-20 rounded-2xl bg-gradient-to-br from-purple-500 to-blue-500 mb-6">
              <Shield className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-4xl sm:text-5xl font-bold mb-4">Privacy Policy</h1>
            <p className="text-lg text-muted-foreground">
              Your privacy matters. Learn how we protect it.
            </p>
            <p className="text-sm text-muted-foreground mt-2">
              Last updated: January 20, 2025
            </p>
          </div>

          {/* Key Privacy Features */}
          <Card className="glass mb-8 p-6 border-2 border-primary/20">
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 text-center">
              <div>
                <div className="text-3xl font-bold gradient-text mb-2">100%</div>
                <p className="text-sm text-muted-foreground">Client-side processing</p>
              </div>
              <div>
                <div className="text-3xl font-bold gradient-text mb-2">0</div>
                <p className="text-sm text-muted-foreground">Files uploaded to servers</p>
              </div>
              <div>
                <div className="text-3xl font-bold gradient-text mb-2">Never</div>
                <p className="text-sm text-muted-foreground">Your data leaves your device</p>
              </div>
            </div>
          </Card>

          {/* Privacy Policy Sections */}
          <div className="space-y-8">
            {sections.map((section, index) => {
              const Icon = section.icon;
              return (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                >
                  <Card className="glass p-6 hover:shadow-xl transition-shadow duration-300">
                    <div className="flex items-start gap-4">
                      <div className="p-3 rounded-lg bg-primary/10 shrink-0">
                        <Icon className="w-6 h-6 text-primary" />
                      </div>
                      <div className="flex-1">
                        <h2 className="text-2xl font-semibold mb-4">{section.title}</h2>
                        <div className="space-y-3">
                          {section.content.map((paragraph, pIndex) => (
                            <p 
                              key={pIndex} 
                              className="text-muted-foreground leading-relaxed"
                              dangerouslySetInnerHTML={{ 
                                __html: paragraph
                                  .replace(/\*\*(.*?)\*\*/g, '<strong class="text-foreground font-medium">$1</strong>')
                              }}
                            />
                          ))}
                        </div>
                      </div>
                    </div>
                  </Card>
                </motion.div>
              );
            })}
          </div>

          {/* GDPR Compliance Notice */}
          <Card className="glass mt-8 p-6 bg-primary/5 border-primary/20">
            <h3 className="text-xl font-semibold mb-3">GDPR Compliance</h3>
            <p className="text-muted-foreground leading-relaxed">
              AltafToolsHub is fully compliant with the General Data Protection Regulation (GDPR). 
              We respect the privacy rights of all users, regardless of location. Our client-side 
              processing approach means we collect minimal data by design, giving you complete control 
              over your information.
            </p>
          </Card>

          {/* Contact Section */}
          <div className="text-center mt-12 p-8 rounded-2xl bg-gradient-to-br from-primary/10 to-primary/5">
            <Mail className="w-12 h-12 text-primary mx-auto mb-4" />
            <h3 className="text-2xl font-semibold mb-2">Questions About Privacy?</h3>
            <p className="text-muted-foreground mb-4">
              We're here to help with any privacy concerns you may have.
            </p>
            <a 
              href="mailto:altaftoolshub@gmail.com?subject=Privacy%20Question%20-%20AltafToolsHub" 
              className="text-primary hover:underline font-medium"
              data-testid="link-privacy-email"
            >
              altaftoolshub@gmail.com
            </a>
          </div>
        </motion.div>
      </div>
    </div>
  );
}

client/src/pages/qr-generator.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useSEO, generateHowToSchema, generateSoftwareApplicationSchema } from "@/hooks/use-seo";
import { QrCode, Download, Copy, Palette, Zap, Shield, Check, X, RefreshCw, ArrowLeft } from "lucide-react";
import QRCode from "qrcode";
import Breadcrumbs from "@/components/seo/breadcrumbs";
import ToolSEO, { toolFAQs } from "@/components/seo/tool-seo";
import PrivacyNotice from "@/components/privacy-notice";
import { WhyUseSection, UseCasesSection, ComparisonSection, HowItWorksSection, commonFeatures } from "@/components/seo/tool-features";
import { ToolFAQ, generateQRGeneratorFAQs } from "@/components/seo/tool-faq";
import { Store, Share2, Wifi, Calendar, MapPin, CreditCard, Type, Settings as SettingsIcon } from "lucide-react";
import { ContactSupportSection } from "@/components/contact-support";

type QRSize = "small" | "medium" | "large";

const sizeMap: Record<QRSize, number> = {
  small: 200,
  medium: 300,
  large: 400
};

export default function QRGenerator() {
  // Generate structured data for SEO
  const howToSchema = generateHowToSchema({
    name: "How to Generate QR Codes Online",
    description: "Create custom QR codes for text, URLs, and more with customizable colors and sizes",
    totalTime: "PT30S",
    steps: [
      { name: "Enter Data", text: "Type or paste the text, URL, or data for your QR code" },
      { name: "Customize Design", text: "Choose size and colors for your QR code" },
      { name: "Generate QR Code", text: "QR code generates automatically as you type" },
      { name: "Download", text: "Download your QR code as a PNG image" }
    ]
  });

  const softwareSchema = generateSoftwareApplicationSchema({
    name: "QR Code Generator - AltafToolsHub",
    description: "Free QR code generator with custom colors and sizes. Create QR codes for URLs, text, WiFi, and more. 100% browser-based.",
    applicationCategory: "BusinessApplication",
    url: "https://www.altaftoolshub.com/qr-generator",
    aggregateRating: { ratingValue: 4.9, ratingCount: 2156, bestRating: 5 },
    featureList: [
      "Generate QR codes for any text or URL",
      "Custom foreground and background colors",
      "Three size options (200px, 300px, 400px)",
      "Real-time QR generation as you type",
      "Download as PNG image",
      "100% client-side processing",
      "No watermarks or limits"
    ],
    datePublished: "2024-01-01",
    dateModified: "2025-01-17"
  });

  useSEO({
    title: "Free QR Code Generator - Create Custom QR Codes Online | AltafToolsHub",
    description: "Free online QR code generator. Create custom QR codes from text, URLs, emails, phone numbers. Customize colors and sizes. 100% client-side for privacy.",
    path: "/qr-generator",
    keywords: "qr code generator, create qr code, free qr generator, online qr code, custom qr code, qr code maker, generate qr code, qr code creator, qr code 2025, ai qr generator",
    ogImage: "https://www.altaftoolshub.com/og-qr-generator.png",
    structuredData: [howToSchema, softwareSchema],
    additionalMetaTags: [
      { name: "application-name", content: "QR Code Generator - AltafToolsHub" },
      { property: "article:section", content: "Utility Tools" },
      { property: "article:tag", content: "QR Code Generation" },
      { property: "article:tag", content: "Marketing Tools" },
      { property: "article:tag", content: "Business Tools" }
    ]
  });

  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [text, setText] = useState("");
  const [size, setSize] = useState<QRSize>("medium");
  const [fgColor, setFgColor] = useState("#000000");
  const [bgColor, setBgColor] = useState("#ffffff");
  const [qrDataUrl, setQrDataUrl] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const { toast } = useToast();

  // Generate QR code whenever input changes
  useEffect(() => {
    if (!text.trim()) {
      setQrDataUrl(null);
      return;
    }

    const generateQR = async () => {
      setIsGenerating(true);
      try {
        const canvas = canvasRef.current;
        if (!canvas) return;

        await QRCode.toCanvas(canvas, text, {
          width: sizeMap[size],
          color: {
            dark: fgColor,
            light: bgColor
          },
          margin: 2,
          errorCorrectionLevel: 'M'
        });

        const dataUrl = canvas.toDataURL('image/png');
        setQrDataUrl(dataUrl);
      } catch (error) {
        console.error('Error generating QR code:', error);
        toast({
          title: "Generation Failed",
          description: "Failed to generate QR code. Please try again.",
          variant: "destructive"
        });
      } finally {
        setIsGenerating(false);
      }
    };

    const debounceTimer = setTimeout(generateQR, 300);
    return () => clearTimeout(debounceTimer);
  }, [text, size, fgColor, bgColor]);

  const handleDownload = () => {
    if (!qrDataUrl) return;

    const link = document.createElement('a');
    link.download = `qr-code-${Date.now()}.png`;
    link.href = qrDataUrl;
    link.click();

    toast({
      title: "Downloaded!",
      description: "QR code has been downloaded successfully.",
    });
  };

  const handleCopyImage = async () => {
    if (!qrDataUrl) return;

    try {
      const blob = await (await fetch(qrDataUrl)).blob();
      await navigator.clipboard.write([
        new ClipboardItem({
          'image/png': blob
        })
      ]);

      toast({
        title: "Copied!",
        description: "QR code copied to clipboard.",
      });
    } catch (error) {
      // Fallback: copy data URL as text
      await navigator.clipboard.writeText(qrDataUrl);
      toast({
        title: "Copied!",
        description: "QR code data copied to clipboard.",
      });
    }
  };

  const handleReset = () => {
    setText("");
    setSize("medium");
    setFgColor("#000000");
    setBgColor("#ffffff");
    setQrDataUrl(null);
  };

  return (
    <div className="min-h-screen pattern-bg">
      <ToolSEO 
        toolName="QR Code Generator"
        description="Generate QR codes instantly from text or URLs with customizable colors and sizes"
        category="UtilitiesApplication"
        faqs={toolFAQs["qr-generator"]}
        rating={{ value: 4.9, count: 456 }}
      />
      <div className="container mx-auto px-4 py-8 max-w-6xl">
        <Breadcrumbs items={[{ name: "QR Generator", url: "/qr-generator" }]} />
        {/* Header */}
        <div className="text-center mb-8">
          <Button 
            variant="ghost" 
            className="mb-4" 
            data-testid="button-back"
            onClick={() => {
              window.location.href = '/';
              setTimeout(() => {
                const toolsSection = document.getElementById('tools-section');
                if (toolsSection) {
                  toolsSection.scrollIntoView({ behavior: 'smooth' });
                }
              }, 100);
            }}
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Tools
          </Button>
          <div className="inline-flex items-center justify-center w-16 h-16 rounded-2xl bg-gradient-to-br from-indigo-500 to-purple-600 mb-4">
            <QrCode className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-4xl font-bold mb-4">QR Code Generator</h1>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Generate QR codes instantly from any text or URL. Customize colors and sizes, all processed securely in your browser.
          </p>
        </div>

        {/* Privacy Notice */}
        <PrivacyNotice message="QR codes are generated locally. Your data remains completely private." />

        {/* Features */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
          <Card className="p-4 glass">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-purple-500 to-blue-500 flex items-center justify-center">
                <Shield className="w-5 h-5 text-white" />
              </div>
              <div>
                <h3 className="font-semibold">100% Private</h3>
                <p className="text-sm text-muted-foreground">Generated in your browser</p>
              </div>
            </div>
          </Card>
          <Card className="p-4 glass">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-blue-500 to-cyan-500 flex items-center justify-center">
                <Zap className="w-5 h-5 text-white" />
              </div>
              <div>
                <h3 className="font-semibold">Instant Generation</h3>
                <p className="text-sm text-muted-foreground">Real-time QR codes</p>
              </div>
            </div>
          </Card>
          <Card className="p-4 glass">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-lg bg-gradient-to-br from-cyan-500 to-teal-500 flex items-center justify-center">
                <Palette className="w-5 h-5 text-white" />
              </div>
              <div>
                <h3 className="font-semibold">Customizable</h3>
                <p className="text-sm text-muted-foreground">Colors and sizes</p>
              </div>
            </div>
          </Card>
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Input Section */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">QR Code Content</h2>

            <div className="space-y-4">
              <div>
                <Label htmlFor="qr-text">Text or URL</Label>
                <Textarea
                  id="qr-text"
                  placeholder="Enter text, URL, or any data to encode..."
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  className="mt-2 min-h-[120px]"
                  data-testid="input-qr-text"
                />
                <p className="text-sm text-muted-foreground mt-1">
                  {text.length} characters
                </p>
              </div>

              <div>
                <Label htmlFor="qr-size">Size</Label>
                <Select value={size} onValueChange={(v) => setSize(v as QRSize)}>
                  <SelectTrigger id="qr-size" className="mt-2" data-testid="select-qr-size">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="small">Small (200x200)</SelectItem>
                    <SelectItem value="medium">Medium (300x300)</SelectItem>
                    <SelectItem value="large">Large (400x400)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="fg-color">Foreground Color</Label>
                  <div className="flex gap-2 mt-2">
                    <Input
                      id="fg-color"
                      type="color"
                      value={fgColor}
                      onChange={(e) => setFgColor(e.target.value)}
                      className="w-16 h-10 p-1 cursor-pointer"
                      data-testid="input-fg-color"
                    />
                    <Input
                      type="text"
                      value={fgColor}
                      onChange={(e) => setFgColor(e.target.value)}
                      placeholder="#000000"
                      className="flex-1"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="bg-color">Background Color</Label>
                  <div className="flex gap-2 mt-2">
                    <Input
                      id="bg-color"
                      type="color"
                      value={bgColor}
                      onChange={(e) => setBgColor(e.target.value)}
                      className="w-16 h-10 p-1 cursor-pointer"
                      data-testid="input-bg-color"
                    />
                    <Input
                      type="text"
                      value={bgColor}
                      onChange={(e) => setBgColor(e.target.value)}
                      placeholder="#ffffff"
                      className="flex-1"
                    />
                  </div>
                </div>
              </div>

              <Button
                onClick={handleReset}
                variant="outline"
                className="w-full"
                data-testid="button-reset-qr"
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                Reset All
              </Button>
            </div>
          </Card>

          {/* Output Section */}
          <Card className="p-6">
            <h2 className="text-xl font-semibold mb-4">Generated QR Code</h2>

            <div className="flex flex-col items-center">
              {text.trim() ? (
                <>
                  <div className="relative mb-6">
                    <canvas
                      ref={canvasRef}
                      className="border rounded-lg shadow-lg"
                      style={{
                        maxWidth: '100%',
                        height: 'auto',
                        display: isGenerating ? 'none' : 'block'
                      }}
                    />
                    {isGenerating && (
                      <div className="flex items-center justify-center w-full h-64 border rounded-lg">
                        <div className="text-center">
                          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
                          <p className="text-muted-foreground">Generating QR code...</p>
                        </div>
                      </div>
                    )}
                  </div>

                  {qrDataUrl && !isGenerating && (
                    <div className="flex gap-4 w-full">
                      <Button
                        onClick={handleDownload}
                        className="flex-1"
                        data-testid="button-download-qr"
                      >
                        <Download className="w-4 h-4 mr-2" />
                        Download PNG
                      </Button>
                      <Button
                        onClick={handleCopyImage}
                        variant="outline"
                        className="flex-1"
                        data-testid="button-copy-qr"
                      >
                        <Copy className="w-4 h-4 mr-2" />
                        Copy Image
                      </Button>
                    </div>
                  )}
                </>
              ) : (
                <div className="flex flex-col items-center justify-center h-64 text-center">
                  <QrCode className="w-16 h-16 text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">
                    Enter text or URL to generate QR code
                  </p>
                </div>
              )}
            </div>
          </Card>
        </div>

        {/* Tips Section */}
        <Card className="mt-8 p-6 bg-gradient-to-br from-primary/5 to-primary/10">
          <h3 className="text-lg font-semibold mb-3">Pro Tips</h3>
          <ul className="space-y-2">
            <li className="flex items-start gap-2">
              <Check className="w-5 h-5 text-green-500 mt-0.5" />
              <span className="text-sm">QR codes can store URLs, text, WiFi credentials, contact info, and more</span>
            </li>
            <li className="flex items-start gap-2">
              <Check className="w-5 h-5 text-green-500 mt-0.5" />
              <span className="text-sm">Higher error correction allows QR codes to work even when partially damaged</span>
            </li>
            <li className="flex items-start gap-2">
              <Check className="w-5 h-5 text-green-500 mt-0.5" />
              <span className="text-sm">Use high contrast colors for better scanning reliability</span>
