import { Link } from "wouter";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { 
  Search, 
  Star, 
  Sparkles, 
  ArrowRight,
  Check,
  Clock,
  Filter,
  Grid3x3,
  List,
  ChevronDown
} from "lucide-react";
import { cn } from "@/lib/utils";
import { HowItWorksSection } from "@/components/seo/tool-features";
import { ToolFAQ } from "@/components/seo/tool-faq";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";

const ToolCard = ({ tool, view }: { tool: any; view: 'grid' | 'list' }) => {
  const Icon = tool.icon;

  const cardContent = (
    <motion.div
      whileHover={tool.available ? { scale: 1.02, y: -3 } : {}}
      whileTap={tool.available ? { scale: 0.98 } : {}}
      className="h-full"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <Card className={cn(
        "relative h-full transition-all duration-300 cursor-pointer group overflow-hidden rounded-lg",
        view === 'grid' ? "p-6 min-h-[240px]" : "p-6",
        "hover:shadow-xl hover:border-primary/30",
        !tool.available && "opacity-60 cursor-not-allowed hover:opacity-60"
      )} data-testid={`tool-card-${tool.id}`}>
        {/* Background Gradient */}
        <div className={cn(
          "absolute inset-0 opacity-0 group-hover:opacity-100 transition-all duration-500",
          "bg-gradient-to-br",
          tool.color
        )} style={{ opacity: 0.03 }}></div>

        {/* Badges */}
        <div className="absolute top-3 right-3 flex gap-2">
          {tool.new && (
            <Badge className="bg-green-500/10 text-green-600 dark:text-green-400 border-green-500/30 text-xs">
              <Sparkles className="w-3 h-3 mr-1" />
              New
            </Badge>
          )}
          {tool.popular && (
            <Badge className="bg-primary/10 text-primary border-primary/30 text-xs">
              <Star className="w-3 h-3 mr-1" />
              Popular
            </Badge>
          )}
          {!tool.available && (
            <Badge variant="secondary" className="text-xs">
              <Clock className="w-3 h-3 mr-1" />
              Soon
            </Badge>
          )}
        </div>

        {view === 'grid' ? (
          <div className="relative">
            {/* Icon with gradient background */}
            <div className={cn(
              "w-14 h-14 mb-4 rounded-xl flex items-center justify-center",
              "bg-gradient-to-br",
              tool.color,
              "shadow-lg"
            )}>
              <Icon className="w-8 h-8 text-white" />
            </div>

            {/* Content */}
            <h3 className="font-semibold text-lg mb-2 group-hover:text-primary transition-colors line-clamp-1">
              {tool.title}
            </h3>
            <p className="text-sm text-muted-foreground truncate-3 mb-3">
              {tool.extendedDescription || tool.description}
            </p>

            {/* Extended Description for SEO and better UX */}
            <div className="text-xs text-muted-foreground/80 space-y-1">
