};

export default function AllToolsPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [sortBy, setSortBy] = useState<'name' | 'category' | 'status'>('category');
  const [statusFilter, setStatusFilter] = useState<'all' | 'available' | 'soon'>('all');

  // Check URL params on mount for category selection and scroll
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const categoryParam = params.get('category');
    if (categoryParam === 'available') {
      setSelectedCategory('available');
      setStatusFilter('available');
    } else if (categoryParam && toolCategories.some(cat => cat.id === categoryParam)) {
      setSelectedCategory(categoryParam);
      // Scroll to the category section after a short delay to ensure DOM is ready
      setTimeout(() => {
        const categoryElement = document.getElementById(`category-${categoryParam}`);
        if (categoryElement) {
          categoryElement.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
      }, 100);
    }
  }, []);

  // Filter and sort tools
  const filteredTools = useMemo(() => {
    let tools = allTools;

    // Filter by search query
    if (searchQuery) {
      tools = tools.filter(tool => 
        tool.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        tool.description.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by category
    if (selectedCategory === "available") {
      tools = tools.filter(tool => tool.available === true);
    } else if (selectedCategory !== "all") {
      tools = tools.filter(tool => tool.category === selectedCategory);
    }

    // Filter by status
    if (statusFilter !== 'all') {
      tools = tools.filter(tool => 
        statusFilter === 'available' ? tool.available : !tool.available
      );
    }

    // Sort tools
    switch (sortBy) {
      case 'name':
        tools = [...tools].sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'status':
        tools = [...tools].sort((a, b) => {
          if (a.available === b.available) return 0;
          return a.available ? -1 : 1;
        });
        break;
      case 'category':
      default:
        // Keep original order (grouped by category)
        break;
    }

    return tools;
  }, [searchQuery, selectedCategory, sortBy, statusFilter]);

  // Group tools by category for display
  const groupedTools = useMemo(() => {
    if (sortBy !== 'category' || selectedCategory !== 'all') {
      return null;
    }

    const groups: Record<string, typeof allTools> = {};
    filteredTools.forEach(tool => {
      if (!groups[tool.category]) {
        groups[tool.category] = [];
      }
      groups[tool.category].push(tool);
    });

    return groups;
  }, [filteredTools, sortBy, selectedCategory]);

  const availableToolsCount = allTools.filter(t => t.available).length;
  const totalToolsCount = allTools.length;

  return (
    <div className="min-h-screen relative">
      {/* Hero background gradient */}
      <div className="absolute inset-0 hero-gradient -z-10" />

      {/* Hero Section */}
      <section className="relative pt-20 pb-16">
        <div className="container-section relative z-10">
          <motion.div 
            className="text-center max-w-4xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Badge className="mb-4 bg-gradient-to-r from-primary/20 to-secondary/20 text-primary border-primary/30">
              <Sparkles className="w-3 h-3 mr-1" />
              {availableToolsCount} Tools Available â€¢ {totalToolsCount - availableToolsCount} Coming Soon
            </Badge>

            <h1 className="text-4xl sm:text-5xl lg:text-6xl font-bold mb-6">
              <span className="gradient-text">All Tools</span> at Your Fingertips
            </h1>

            <p className="text-xl text-muted-foreground mb-8 leading-relaxed">
              Explore our complete collection of privacy-first tools. Every tool processes your files locally 
              in your browser - nothing is ever uploaded to servers.
            </p>

            {/* Search Bar */}
            <div className="max-w-2xl mx-auto mb-8">
              <div className="relative">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
                <Input
                  type="text"
                  placeholder="Search tools... (e.g., PDF, compress, convert)"
                  className="pl-12 pr-4 py-6 text-lg rounded-xl bg-background/80 backdrop-blur-sm border-primary/20"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  data-testid="input-search-tools"
                />
              </div>
            </div>

            {/* Controls Bar */}
            <div className="flex flex-wrap items-center justify-center gap-4 mb-8">
              {/* Category Filter */}
              <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                <SelectTrigger className="w-[200px]" data-testid="select-category">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  <SelectItem value="available" className="font-semibold text-green-600 dark:text-green-400">
                    âœ… Available Tools ({allTools.filter(t => t.available).length})
                  </SelectItem>
                  {toolCategories.map((category) => (
                    <SelectItem key={category.id} value={category.id}>
                      {category.name} ({category.tools.length})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              {/* Status Filter */}
              <Select value={statusFilter} onValueChange={(value: 'all' | 'available' | 'soon') => setStatusFilter(value)}>
                <SelectTrigger className="w-[180px]" data-testid="select-status-filter">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all" data-testid="filter-all">All Tools</SelectItem>
                  <SelectItem value="available" data-testid="filter-available">Available</SelectItem>
                  <SelectItem value="soon" data-testid="filter-soon">Coming Soon</SelectItem>
                </SelectContent>
              </Select>

              {/* Sort By */}
              <Select value={sortBy} onValueChange={(value: any) => setSortBy(value)}>
                <SelectTrigger className="w-[180px]" data-testid="select-sort">
                  <SelectValue placeholder="Sort by" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="category">By Category</SelectItem>
                  <SelectItem value="name">By Name</SelectItem>
                  <SelectItem value="status">By Status</SelectItem>
                </SelectContent>
              </Select>

              {/* View Mode */}
              <div className="flex items-center gap-2 border rounded-lg p-1">
                <Button
                  variant={viewMode === 'grid' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('grid')}
                  className="px-3"
                  data-testid="button-view-grid"
                >
                  <Grid3x3 className="w-4 h-4" />
                </Button>
                <Button
                  variant={viewMode === 'list' ? 'default' : 'ghost'}
                  size="sm"
                  onClick={() => setViewMode('list')}
                  className="px-3"
                  data-testid="button-view-list"
                >
                  <List className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Tools Grid/List */}
      <section className="py-12 relative z-10">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          <AnimatePresence mode="wait">
            {groupedTools && sortBy === 'category' && selectedCategory === 'all' ? (
              // Grouped by Category View
              <motion.div 
                key="grouped"
                className="space-y-12"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                {toolCategories.map((category) => {
                  const categoryTools = groupedTools[category.id] || [];
                  if (categoryTools.length === 0) return null;

                  return (
                    <div key={category.id} id={`category-${category.id}`} className="space-y-6 scroll-mt-20">
                      <div className="flex items-center gap-3">
                        <category.icon className="w-6 h-6 text-primary" />
                        <h2 className="text-2xl font-bold">{category.name}</h2>
                        <Badge variant="secondary" className="ml-2">
                          {categoryTools.length} tools
                        </Badge>
                      </div>

                      <div className={cn(
                        viewMode === 'grid' 
                          ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6"
                          : "space-y-3"
                      )}>
                        {categoryTools.map((tool) => (
                          <ToolCard key={tool.id} tool={tool} view={viewMode} />
                        ))}
                      </div>
                    </div>
                  );
                })}
              </motion.div>
            ) : (
              // Flat View
              <motion.div 
                key="flat"
                className={cn(
                  viewMode === 'grid' 
                    ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6"
                    : "space-y-3"
                )}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
              >
                {filteredTools.map((tool) => (
                  <ToolCard key={tool.id} tool={tool} view={viewMode} />
                ))}
              </motion.div>
            )}
          </AnimatePresence>

          {/* No Results */}
          {filteredTools.length === 0 && (
            <motion.div 
              className="text-center py-20"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
            >
              <p className="text-xl text-muted-foreground">
                No tools found matching your criteria. Try adjusting your filters.
              </p>
            </motion.div>
          )}
        </div>
      </section>

      {/* Tool Highlights section can be added later */}

      {/* How It Works */}
      <HowItWorksSection
        toolName="All Tools"
        steps={[
          {
            number: 1,
            title: "Browse Tools",
            description: "Explore our collection of 60+ tools organized by category",
            icon: Search
          },
          {
            number: 2,
            title: "Select Your Tool",
            description: "Click on any available tool to start using it instantly",
            icon: Grid3x3
          },
          {
            number: 3,
            title: "Process Locally",
            description: "All processing happens in your browser for 100% privacy",
            icon: Check
          }
        ]}
      />

      {/* FAQ Section */}
      <ToolFAQ
        toolName="All Tools"
        toolPath="/all-tools"
        faqs={[
          {
            question: "Are all tools free to use?",
            answer: "Yes! All available tools on AltafToolsHub are completely free to use with no hidden charges or subscriptions."
          },
          {
            question: "Do I need to create an account?",
            answer: "No account needed! All tools work instantly without any sign-up or login requirements."
          },
