                        </div>

                        <div className="mb-6">
                          <h3 className="font-medium mb-3">Click pages to select/deselect</h3>
                          <div className="grid grid-cols-4 sm:grid-cols-6 md:grid-cols-8 gap-4 max-h-96 overflow-y-auto p-2">
                            {pageThumbnails.map((thumb) => (
                              <div
