    a.href = url;
    a.download = result.name;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const downloadAllResults = () => {
    splitResults.forEach(result => downloadResult(result));
  };

  const reset = () => {
    setPdfFile(null);
    setPdfDoc(null);
    setTotalPages(0);
    setSplitResults([]);
