        >
          <EyeOff className="mr-2 h-4 w-4" />
          Enable Motion
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handlePreferenceChange(true)}
          className={userPreference === true ? "bg-accent" : ""}
        >
          <Eye className="mr-2 h-4 w-4" />
