}

.feature-card:hover {
  transform: translateY(-8px) scale(1.02);
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.15) 0%, rgba(59, 130, 246, 0.08) 100%);
  box-shadow: 
    0 20px 40px -10px rgba(139, 92, 246, 0.25),
    0 15px 30px -10px rgba(59, 130, 246, 0.15),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.7);
  border-color: rgba(139, 92, 246, 0.3);
}

.dark .feature-card {
  background: rgba(30, 41, 59, 0.9);
  border: 1px solid rgba(139, 92, 246, 0.2);
  box-shadow: 
    0 10px 30px -5px rgba(0, 0, 0, 0.5),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.1);
}

.dark .feature-card:hover {
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.15) 0%, rgba(59, 130, 246, 0.08) 100%);
  border-color: rgba(139, 92, 246, 0.5);
  box-shadow: 
    0 20px 40px -10px rgba(139, 92, 246, 0.3),
    0 15px 30px -10px rgba(59, 130, 246, 0.2),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.15);
}

/* Drag and drop area */
.drag-area {
  @apply rounded-xl transition-all duration-300;
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.03) 0%, rgba(59, 130, 246, 0.03) 100%);
  border: 2px dashed rgba(139, 92, 246, 0.3);
}

.drag-area:hover {
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.08) 0%, rgba(59, 130, 246, 0.08) 100%);
  border-color: rgba(139, 92, 246, 0.5);
}

