    "interactionStatistic": {
      "@type": "InteractionCounter",
      "interactionType": { "@type": "WatchAction" },
      "userInteractionCount": 5243
    }
  };

  // Enhanced Review schema with aggregate ratings
  const reviewSchema = {
    "@context": "https://schema.org",
    "@type": "Product",
    "name": "PDF Compressor Tool",
    "brand": {
      "@type": "Brand",
      "name": "AltafToolsHub"
    },
    "aggregateRating": {
      "@type": "AggregateRating",
      "ratingValue": "4.9",
      "reviewCount": "1523",
      "bestRating": "5",
      "worstRating": "1"
    },
    "review": [
      {
        "@type": "Review",
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "5",
          "bestRating": "5"
        },
        "author": {
          "@type": "Person",
          "name": "John Smith"
        },
        "datePublished": "2025-01-14",
        "reviewBody": "Excellent compression tool! Reduced my 20MB PDF to 500KB without noticeable quality loss."
      },
      {
        "@type": "Review",
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "5",
          "bestRating": "5"
        },
        "author": {
          "@type": "Person",
          "name": "Emma Wilson"
        },
        "datePublished": "2025-01-12",
        "reviewBody": "The target size feature is amazing. I can compress to exactly 100KB for email attachments."
      }
    ],
    "offers": {
      "@type": "Offer",
      "price": "0",
      "priceCurrency": "USD",
      "availability": "https://schema.org/InStock"
    }
  };

  useSEO({
