  id: string;
  title: string;
  description: string;
  icon: LucideIcon;
  href: string;
  color: string;
  available: boolean;
  category: string;
  new?: boolean;
}

type NavTool = Tool | CustomTool;

// Helper component for navigation items - compact layout without descriptions
const ToolNavItem = ({ tool, onClick }: { tool: NavTool; onClick?: () => void }) => {
  const Icon = tool.icon;

  const content = (
    <div className="group flex items-center gap-2 px-3 py-2 rounded-md hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors">
      <div className={cn(
        "w-6 h-6 rounded flex items-center justify-center flex-shrink-0",
        "bg-gradient-to-br shadow-sm", tool.color
      )}>
        <Icon className="w-3.5 h-3.5 text-white" />
      </div>
      <div className="flex-1 min-w-0">
        <div className="text-sm font-medium flex items-center gap-2">
          <span className="truncate">{tool.title}</span>
          {tool.new && (
            <span className="text-xs px-1.5 py-0.5 rounded-full bg-emerald-600 text-white font-medium">
              New
            </span>
          )}
          {!tool.available && (
            <span className="text-xs px-1.5 py-0.5 rounded-full bg-gray-500 text-white font-medium">
              Soon
            </span>
          )}
        </div>
      </div>
    </div>
  );

  if (!tool.available) {
    return (
      <button
        className="w-full cursor-not-allowed opacity-50"
        disabled
        aria-disabled="true"
        data-testid={`nav-${tool.id}-disabled`}
        onClick={(e) => e.preventDefault()}
      >
        {content}
      </button>
    );
  }

  return (
    <Link
      href={tool.href}
      className="block w-full"
      data-testid={`nav-${tool.id}`}
      onClick={onClick}
    >
      {content}
